<?php 	class Controller_Add_to_basket extends Controller	{		public $model;    	public $view;        	function __construct()    	{        	$this->view = new View();        	$this->model = new Model_Add_to_basket();    	}    	    	function action_add($data)    	{	    		$data=$this->model->add_data($data);        	$this->view->generate('add_to_basket_view.php', 'add_to_basket_view.php',$data);    	}    	function action_get($data)    	{	    		echo count($_SESSION['cart']);    	}    	function action_delete($data)    	{	    		$data=$this->model->delete_data($data);        	$this->view->generate('delete_to_basket_view.php', 'delete_to_basket_view.php',$data);    	}	}?>