<?php 	class Controller_Tovar extends Controller	{		public $model;    	public $view;        	function __construct()    	{        	$this->view = new View();        	$this->model = new Model_Tovar();    	}    	function action_index()    	{				$data = $this->model->get_data();        	$this->view->generate('tovar_view.php', 'template_view.php', $data);    	}	public function action_details($data)	{		//$data = $this->model->get_data($data);		//$this->view->generate('tovar_view.php', 'template_view.php', $data);	}	public function action_page($data)	{		$data = $this->model->get_page($data);		$this->view->generate('tovar_view.php', 'template_view.php', $data);	}}?>