<?php 	class Controller_Contacts extends Controller	{		public $model;    	public $view;        	function __construct()    	{        	$this->view = new View();        	$this->model = new Model_Contact();    	}    	    	function action_index()    	{	    		$data=$this->model->get_data();        	$this->view->generate('contacts_view.php', 'template_view.php',$data);    	}	}?>