<?php 	function get_content()	{		$form = file_get_contents('application/views/templates/tovar_edit_form.html');		$photo = "<img id='photo'  src='".baseaddress."/images/not-found.png' alt='notimg'/>";		$form = str_replace("##srcimg##",$photo,$form);				$categories=Model_Admin::get_categories();		$tovarCategories=Model_Admin::get_categories($data['id']);		$optionCategory="";		foreach($categories as $category)		{			$optionCategory=$optionCategory."<option value='".$category['0']."'>".$category['1']."</option>";		}		$form = str_replace("##Category##",$optionCategory,$form);		$form = str_replace("##title##","New title",$form);		$form = str_replace("##price##","0.00",$form);		$form = str_replace("##costprice##","0.00",$form);		$form = str_replace("##details##","description",$form);		$form = str_replace("##action##",baseaddress."/admin/addtovar/",$form);		$form = str_replace("##id##", "",$form);		$form = str_replace("##required##", "required",$form);		$form = str_replace("##value##", "Add",$form);		//##value##		echo $form;	}		function buttons_content()	{		$button = file_get_contents('application/views/button.html');		$button=str_replace("##title##","Edit tovars", $button);		$button=str_replace("##href##",baseaddress."/admin/", $button);		echo $button;	}	function get_title()	{		return "Add new";	}?>