<?php 	function get_content($data)	{		$tovarcontrols = file_get_contents('application/views/templates/tovars_controls.html');		echo "<a class='three column secondary button' href='".baseaddress."/admin/addtovar'>Add new</a>";				foreach($data as $item)		{			$tovar = str_replace("##title##", $item['title'],$tovarcontrols);			$tovar = str_replace("##details##", $item['description'],$tovar);			$tovar = str_replace("##hrefedit##", baseaddress."/admin/edittovar/##id##/##titlealias##",$tovar);			$tovar = str_replace("##hrefdelete##", baseaddress."/admin/deletetovar/##id##/##titlealias##",$tovar);			$tovar = str_replace("##id##", $item['id'],$tovar);			$tovar = str_replace("##titlealias##", str_replace(" ","-",$item['title']),$tovar);						//##hrefedit##			echo $tovar;		}		echo "<a class='three column secondary button' href='".baseaddress."/admin/addtovar'>Add new</a>";	}		function buttons_content()	{		echo "<hr/>";	}	function get_title()	{		return "Admin panel";	}?>