<?php 	function get_content($data)	{		$form = file_get_contents('application/views/templates/tovar_edit_form.html');		$photo = Model_Admin::get_photo($data['id']);		$form = str_replace("##srcimg##",$photo,$form);				$categories=Model_Admin::get_categories();		$tovarCategories=Model_Admin::get_categories($data['id']);		$optionCategory="";		foreach($categories as $category)		{			$optionCategory=$optionCategory."<option ";			$key=array_search (array($category['0']), $tovarCategories);			if(is_int($key))			{				$optionCategory=$optionCategory." selected ";			}			$optionCategory=$optionCategory." value='".$category['0']."'>".$category['1']."</option>";		}		$form = str_replace("##Category##",$optionCategory,$form);		$form = str_replace("##title##",$data['title'],$form);		$form = str_replace("##price##",$data['price'],$form);		$form = str_replace("##costprice##",$data['cost_price'],$form);		$form = str_replace("##details##",$data['description'],$form);		$form = str_replace("##action##",baseaddress."/admin/edittovar/",$form);		$form = str_replace("##id##", $data['id'],$form);		$form = str_replace("##required##", "",$form);		$form = str_replace("##value##", "Save",$form);		//##action##		echo $form;	}		function buttons_content()	{		$button = file_get_contents('application/views/templates/button.html');		$button=str_replace("##title##","Edit tovars", $button);		$button=str_replace("##href##",baseaddress."/admin/", $button);		echo $button;	}	function get_title()	{		return "Edit tovar  ".str_replace("-", " ",Route::$routes[4]);	}?>