as $item)	{		$tmp = str_replace("##item##",$item['title'],$right_item);		$tmp = str_replace("##href##",baseaddress."/tovar/category/".$item['id']."/".$item['title'],$tmp);		$content=$content.$tmp;	}	$rightMenu=str_replace("##items##",$content,$right);	$rightMenu=str_replace("##rightMenu##","Category",$rightMenu);	echo $rightMenu;	?>