<?php 	function get_title()	{		if(Route::$routes[2]=='details')			return str_replace("-", " ",Route::$routes[4]);		if(Route::$routes[2]=='category')			return str_replace("-", " ",Route::$routes[4]);		return "Online Shop";	}	function get_content($data)	{		if(Route::$routes[2]!='details')		{			$count=0;			$feature = file_get_contents('application/views/templates/feature_content.html');			foreach($data as $item)			{				if($count==6)					break;				$count++;				$photo=Model_Tovar::get_photo($item['id']);				$tovar = str_replace("##srcimg##", $photo ,$feature);				$tovar = str_replace("##href##", baseaddress."/tovar/details/##id##/##titlealias##",$tovar);				$tovar = str_replace("##title##", $item['title'],$tovar);				$tovar = str_replace("##titlealias##", str_replace(" ","-",$item['title']),$tovar);				$tovar = str_replace("##id##", $item['id'],$tovar);				$tovar = str_replace("##price##", "$".$item['price'],$tovar);				echo $tovar;			}			if(count($data)==0)			{				echo "<h5 class='text-center'>Not found</h5>";			}		}		else		{			foreach($data as $item)			{				$feature = file_get_contents('application/views/templates/details_content.html');				$photo=Model_Tovar::get_photo($item['id']);				$feature = str_replace("##srcimg##", $photo ,$feature);				$feature = str_replace("##details##", $item['description'],$feature);				$feature = str_replace("##id##", $item['id'],$feature);				$feature = str_replace("##price##", "$".$item['price'],$feature);				//##details##				echo $feature;			}			if(count($data)==0)			{				echo "<h5 class='text-center'>Not found</h5>";			}		}	}		function buttons_content()	{		if(Route::$routes[2]!='details')		{			$tmp = file_get_contents('application/views/templates/button.html');			for($i=1;$i<=Model_Tovar::$pages;$i++)			{				$button=str_replace("##title##","".$i, $tmp);				$button=str_replace("##href##",baseaddress."/tovar/page/".$i, $button);				echo $button;			}		}		else		{			$button = file_get_contents('application/views/templates/button.html');			$button=str_replace("##title##","See all tovar", $button);			$button=str_replace("##href##",baseaddress."/tovar/", $button);			echo $button;		}	}?>